import React, { useState } from 'react'
import './NewData.css'

const NewData = (props) => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
        // console.log(enteredTitle);
    }
    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value);
        // console.log(enteredAmount);
    }
    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
        // console.log(enteredDate);
    }
    const submitHandler = (event) => {
        event.preventDefault();
        // console.log("submitted")
        const ExpenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        }
        props.onNewExpenseData(ExpenseData);
        // console.log(ExpenseData);
        setEnteredTitle('');
        setEnteredAmount('');
        setEnteredDate('');


    }

    return (
        <>
            <form onSubmit={submitHandler}>
                <div className="new-newdata__controls">
                    <div className="new-newdata__control">
                        <label htmlFor="">First Name</label>
                        <input type="text" value={enteredTitle} onChange={titleChangeHandler} />
                    </div>
                    <div className="new-newdata__control">
                        <label htmlFor=""><Last></Last> Name</label>
                        <input type="text" value={enteredTitle} onChange={titleChangeHandler} />
                    </div>
                    <div className="new-newdata__control">
                        <label htmlFor="">Amount</label>
                        <input type="number" value={enteredAmount} min={0.00} step={0.05} onChange={amountChangeHandler} />
                    </div>
                    <div className="new-newdata__control">
                        <label htmlFor="">Date</label>
                        <input type="date" value={enteredDate} min={'2019-01-01'} max={'2022-12-31'} onChange={dateChangeHandler} />
                    </div>
                </div>
                <div className="new-newdata__actions">
                    <button type='submit' onClick={props.onCancel}>Cancel</button>
                    <button type='submit'>Add Expenses</button>
                </div>
            </form>
        </>
    );
}

export default NewData;
