import React, { useEffect } from "react";
import './ShowData.css'
import { useSelector, useDispatch } from "react-redux";
import axios from "axios";

const ShowData = () => {
  const dispatch = useDispatch();
  const isFirstCall = useSelector((state) => state.isFirstCall);

  useEffect(() => {
    const getData = async () => {
      dispatch({ type: 'FIRSTCALL', isFirstCall: false })
      await axios
        .get("https://reqres.in/api/users")
        .then((data) => {
          dispatch({
            type: 'DATA_RECEIVED', items: data.data.data
          });
        })
        .catch((err) => {
          console.log("Err: ", err);
        });
    };
    // getData();
    isFirstCall ? getData() : console.log("");
  }, [])
  const infoList = useSelector((state) => state.data);
  function removeHandler(id) {
    dispatch({
      type: 'DELETE_ITEM', id: id
    });
    console.log(id)
  }
  var isActiveEdit = false;
  const editActiveHAndler = (e) => {
    isActiveEdit = true;
  }
  return (
    <div>
      {infoList.map((element) => (
        <li className="showdata-list" key={element.id} onClick={editActiveHAndler}>
          <div className='showdata-item' >
            {isActiveEdit}
              <input type="text" value={element.first_name} onChange={editFirstNameChangeHandler} />
              <input type="text" value={element.last_name} onChange={editLastNameChangeHandler} />
              <input type="text" value={element.email} onChange={editEmailChangeHandler} />
            }
            <h2 >{element.first_name} </h2><h2 >{element.last_name}</h2>
            <h2>{element.email}</h2>
            <button className='showdata-item__btn' onClick={() => removeHandler(element.id)} >D</button>
          </div>
        </li>))}
    </div>
  )
}
export default ShowData;
