import './NewData.css'
import "./ShowData.css";
import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";

const ListData = (props) => {
    const [keyVal, setKeyVal] = useState(props.keyId);
    const [fstName, setFstName] = useState(props.firstName);
    const [lstName, setLstName] = useState(props.lastName);
    const [emailId, setEmaiId] = useState(props.emailData);
    const [isActive, setIaActive] = useState(false);
    const dispatch = useDispatch();
    console.log(props.keyId)
    // const infoList = useSelector((state) => state.data);
    function removeHandler(keyVal) {
        dispatch({
            type: 'DELETE_ITEM', id: keyVal
        });
        // console.log(keyVal)
    }

    const firstNameChangeHandler = (event) => {
        setfirstName(event.target.value);
        // console.log(firstName);
    }
    const lastNameChangeHandler = (event) => {
        setlastName(event.target.value);
        // console.log(lastName);
    }
    const emailChangeHandler = (event) => {
        setEnteredEmail(event.target.value);
        // console.log(enteredEmail);
    }
    const editActiveHAndler = () => {
        setIaActive(true);
    }

    return (<>
        <ul key={keyVal}>
            <div className='showdata-item' >
                {/* isActive */}
                <div className="new-newdata__controls" onClick={editActiveHAndler}>
                    <div className="new-newdata__control">
                        <input type="text" value={fstName} onClick={editActiveHAndler}/>
                    </div>
                    <div className="new-newdata__control">
                        <input type="text" value={lstName} onClick={editActiveHAndler} />
                    </div>
                    <div className="new-newdata__control">
                        <input type="email" value={emailId} onClick={editActiveHAndler} />
                    </div>
                </div>
                {/* <h2 >{fstName} </h2><h2 >{lstName}</h2>
                <h2>{emailId}</h2> */}
                <button className='showdata-item__btn' onClick={() => removeHandler(keyVal)} >D</button>
            </div>
        </ul>

    </>);
}
export default ListData;


