import React, { useEffect } from "react";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
// // import {connect } from 'react-redux'
// import ShowData from "./ShowData";
export const fetchData = () => (dispatch) => {
    const rowData = useSelector((state) => state.data);
    // const dispatch = useDispatch();
    dispatch({ type: 'FETCH_DATA' });
    const getData = async () => {
        await axios
            .get("https://reqres.in/api/users")
            .then((data) => {
                dispatch({
                    type: 'DATA_RECEIVED', items: data.data
                });
            })
            .catch((err) => {
                console.log("Err: ", err);
            });

    };
    // useEffect(() => {
    getData();
    // }, []);
};

let initialState = {
    data: [
        { "id": 1, "email": "george.bluth@reqres.in", "first_name": "George", "last_name": "Bluth" },
    ]
}


const listReducer = (state = initialState, action) => {
    if (action.type === 'INCREMENT') {
        return {

        };
    }
    if (action.type === 'DECREMENT') {
        return {

        };
    }
    if (action.type === 'FETCH_DATA') {
        return { ...state, itemsRequested: true }
    }
    if (action.type === 'DATA_RECEIVED') {
        return { ...state, itemsRequested: false, items: action.items }
    }
    return state;
}

export default listReducer;
