import React, { useEffect } from "react";
import './ShowData.css'
import { useSelector, useDispatch } from "react-redux";
import axios from "axios";

const ShowData = () => {
  const dispatch = useDispatch();
  const isFirstCall = useSelector((state) => state.isFirstCall);

  useEffect(() => {
    const getData = async () => {
      dispatch({ type: 'FIRSTCALL', isFirstCall: false })
      await axios
        .get("https://reqres.in/api/users")
        .then((data) => {
          dispatch({
            type: 'DATA_RECEIVED', items: data.data.data
          });
        })
        .catch((err) => {
          console.log("Err: ", err);
        });
    };
    // getData();
    isFirstCall ? getData() : console.log("");
  }, [])
  const infoList = useSelector((state) => state.data);
  const delChangeHandler = (ev) => {

  }
  return (
    <div>
      {infoList.map((element) => (
        <li className="showdata-list" key={element.id}>
          <div className='showdata-item'>
            <h2>{element.first_name} {element.last_name}</h2>
            <div className='showdata-item__description'>{element.email}</div>
            <button className='showdata-item__btn' onClick={delChangeHandler} >D</button>
          </div>
        </li>))}
    </div>
  )
}
export default ShowData;
