import React, { useEffect } from "react";
import './ShowData.css'
import { Link } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { fetchData } from "./FetchData";
import axios from "axios";

const ShowData = () => {

  const dispatch = useDispatch();
  useEffect((dispatch) => {
    // dispatch({ type: 'FETCH_DATA' });
    const getData = async () => {
      await axios
        .get("https://reqres.in/api/users")
        .then((data) => {
          dispatch({
            type: 'DATA_RECEIVED', items: data.data
          });
        })
        .catch((err) => {
          console.log("Err: ", err);
        });

    };
    getData();

  }
    , [])
  const infoList = useSelector((state) => state.data);
  console.log(infoList[0])
  for (const element of infoList) {
    console.log(element.id);
  }
  return (
    <div>
      {infoList.map((element) => (
        <li>
          <div className='showdata-item'>
            <h2>{element.first_name}</h2>
            <div className='showdata-item__description'>{element.last_name}</div>
          </div>
          <button onClick={fetchData}>GET API DATA</button>
        </li>))}
    </div>
  )
}
export default ShowData;
